function checkGridAPI_0(){
var var1,row;
var1=F.CID.read();
row=F.COLLEGE_NAME.read();

/******Methods********/

if (var1.length==1){
F.GR6.addDuplicateRow(1);  																			
Ext.Msg.alert('alert',"addDuplicateRow(row) API called. Duplicate row of the row1 has been added");                           //addDuplicateRow(row) API is called
}

if (var1.length==2){																				
Ext.Msg.alert('alert',"getID(), getName(), getColumnCount() APIs called. ID: " + F.GR6.getID() + "\nName: " + F.GR6.getName() + "\n TOtal Columns: " + F.GR6.getColumnCount());      //getID(), getName(), getColumnCount() API s are called
}

if (var1.length==3){																				
for(i=0; i<F.GR6.getRowCount(); i++) 				//getRowCount() API is called
{ 
F.GR6.disableRow(i); 							//disableRow() API is called
} 
}

if (var1.length==4){
for(i=0; i<F.GR6.getRowCount(); i++) 				//getRowCount() API is called
{ 
F.GR6.enableRow(i); 							//enableRow() API is called
} 
}

if (var1.length==5){
Ext.Msg.alert('alert',"getPageCount() API called. The total pages are: " + F.GR6.getPageCount());	//getPageCount() API called
}

if (var1.length==6){
//F.GR6.markForDeletion(row)									//markForDeletion(row) API is called
row=F.COLLEGE_NAME.read();
if(F.GR6.isMarkedForDeletion(row)) 								//isMarkedForDeletion(row) API is called
{ 
Ext.Msg.alert('alert',"isMarkedForDeletion(row) API called. Row is marked for deletion. "); 
} 
else
{
Ext.Msg.alert('alert',"isMarkedForDeletion(row) API called. Row is not marked for deletion."); 
}
}

if (var1.length==7){
var page = F.GR6.findPage(row);								//findPage(row) API is called
F.GR6.showPage(page);										//showPage(page) API is called
Ext.Msg.alert('alert',"findPage() and showPage() APIs called. Showing the page with row " + row + "." );
}

if (var1.length==8){
F.GR6.addRow(true,true);									//addRow() API is called
}

if (var1.length==9){
F.GR6.showFirstPage();										//showFirstPage() API is called
Ext.Msg.alert('alert',"showFirstPage() API called. Showing the first page.");
}

if (var1.length==10){
F.GR6.showPageWithRow(row);								//showPageWithRow() API is called
Ext.Msg.alert('alert',"showPageWithRow() API called. Showing the page with row " + row + ".");
}

if (var1.length==11){
for(r in F.GR6.rows()) 										//F.MR.rows() API is called
{ 
F.STAFF_ID.disable(r);																				
} 
Ext.Msg.alert('alert',"F.MR.rows() API called. Staff ID of all row has been disabled");
}

if (var1.length==12){
F.GR6.showLastPage();										//showLastPage() API called.
Ext.Msg.alert('alert',"showLastPage() API called. Showing the last page.");
}


if (var1.length==13){
F.STAFF_SPECIALITIES.hide();									//F.Field.hide() API called
Ext.Msg.alert('alert',"F.Field.hide() API called. Staff Specialities field has been disabled");
}

if (var1.length==14){
F.GR6.disableImportLink("College Form 1");                                                                //disableImportLink() API called. Import link is enabled
Ext.Msg.alert('alert',"disableImportLink() API called. Import link is disabled");
}

if (var1.length==15){
F.GR6.enableImportLink("College Form 1");                                                        //enableImportLink() API called. Import link is disabled
Ext.Msg.alert('alert',"enableImportLink() API called. Import link is enabled");
}

if (var1.length==16){
F.GR6.makeEmpty();										//makeEmpty() API is called
}

}


