F.STAFF_EMAIL.onChange(function(){                                                                                          //onChange() API Grid field called
F.triggerEvent("onChange",[F.STAFF_NAME.write("OnChange event triggered",2)]);                   //triggerEvent() API called
checkGridField();
});
function checkGridField(){
var fld1, page, row, page1, row1;
fld1=F.STAFF_EMAIL.read();
page=F.STAFF_ID.findPage(5);
//page1=F.STAFF_NAME.findPageIgnoreCase("mno");
row=F.STAFF_NAME.findRow("abc");
row1=F.STAFF_NAME.findRowIgnoreCase("xyz");
if (fld1.length==1){
Ext.Msg.alert('alert',"onChange() for Grid field called");
}
if (fld1.length==2){
if(!F.STAFF_ID.isUnique(2))                                                                       //isUnique() API called
Ext.Msg.alert('alert',"Staff Id in row no. 2 exists in another row. Its not unique.");
else
Ext.Msg.alert('alert',"Staff Id in row no. 2 does not exist in another row. Its unique.");
}
if (fld1.length==3){
Ext.Msg.alert('alert',"The 5th row of the 'Staff' grid present in page no. : "+page);
}
/*if (fld1.length==4){
Ext.Msg.alert('alert',"The page number where staff name 'mno' exists is : "+page1);
}*/
if (fld1.length==5){
Ext.Msg.alert('alert',"The Staff name 'abc' exists in the row no. : "+row);
}
if (fld1.length==6){
Ext.Msg.alert('alert',"The Staff name 'xyz' exists in the row no. : "+row1);
}
}

