car_lov=F.STAFF_QUALIFICATION_LOV.readDisplayedValue(1);                           //readLov() API called deprecated. Used readDisplayedValue instead of it, as mentioned in Help document
car_val=F.STAFF_QUALIFICATION_LOV.readValue(1);                                        //readValue() API called
//car_id=F.STAFF_ID.getMultiRowId();                                                                         //getMultiRowId() function called
//field=F.STAFF_EMAIL.isField();
part=F.STAFF_NAME.partOf("Te");
partIgnore=F.STAFF_NAME.partOfIgnoreCase("Ve,ER,fY")

if (dum1.length==1){
