}

if (dum1.length==2){
Ext.Msg.alert('alert',"readDisplayedValue() API called. The displayed value of the STAFF_DESIGNATION_DD is : "+car_disp);            //readDisplayedValue() API called.
F.STAFF_NAME.write(car_stor,1);                                                                                        //write() API called.
F.STAFF_SPECIALITIES.write(car_lov,1);     
F.STAFF_ADDRESS_TEXTAREA.writeValue(car_val,1);                                                                   //writeVal() API called                                                                                                             
}
if (dum1.length==3){
var boolean1=F.STAFF_NAME.isRequired();                                                                                    //isRequired() called
if(boolean1=="true"){
