F.STAFF_ADDRESS_TEXTAREA.writeAll("D_1000;D_1001;D_1002",";");
}
if (dum1.length==5){
var testVal = F.STAFF_QUALIFICATION_LOV.read();
var testArr = testVal.split(F.STAFF_QUALIFICATION_LOV.getMultiLOVDelimiter());             //getMultiLOVDelimiter() API works for LOV, but not for MLOV
//var testArr = testVal.split(F.BIRTHPLACE.getMultiLOVDelimiter()); 
for(j=0;j<testArr.length;j++) 
