Ext.Msg.alert('alert',"You selected Appstudio");
else
Ext.Msg.alert('alert',"You selected value other than Appstudio");
}
if (dum1.length==8){
if(F.STAFF_QUALIFICATION_LOV.isIgnoreCase("MocKuP"))                                                          //isIgnoreCase() called
Ext.Msg.alert('alert',"You selected Mockup");
else
Ext.Msg.alert('alert',"You selected value other than Mockup");
