DATA_FORM_TEMPLATES.customJs:
F.ORGANIZATION_ID.onChange(function(){                                                           //onChange() API called
checkFormField();
});

function checkFormField(){
var var1,user_id;
var1=F.ORGANIZATION_ID.read();
user_id=F.DD_CURRENT_USER_NAME.read()

if (var1.length==1){
Ext.Msg.alert('alert',"setPrimaryTitle() API called. The primary title of the form changed to 'Test Primary Title'");                                                       //setPrimaryTitle() API called
F.setPrimaryTitle("Test_Primary_Title");
}
if (var1.length==2){
Ext.Msg.alert('alert',"setSecondaryTitle() API called. The secondary title of the form changed to 'Test Secondary Title'");                                                //setSecondaryTitle() API called
F.setSecondaryTitle("Test_Secondary_Title");
}
if (var1.length==3){
Ext.Msg.alert('alert',"setActionBtnAttributes() API called. The button labels of 'Save', 'Submit' and 'Close' buttons changed");                  //setActionBtnAttributes() API called
F.setActionBtnAttributes({save:{label:'Custom-save'},submit:{label:'Custom-submit'},close:{label:'Custom-close'}});
}
if (var1.length==4){
Ext.Msg.alert('alert',"setActionBtnAttributes() API called. The button labels of 'Close' button changed");                                                //setActionBtnAttributes() API called
F.setActionBtnAttributes({close:{label:"Cancel"}});
}
if (var1.length==5){
Ext.Msg.alert('alert',"setActionBtnAttributes() API called. The 'Save' button is hidden");                                                                     //setActionBtnAttributes() API called
F.setActionBtnAttributes({save:{hidden:true}});
}
if (var1.length==6){
Ext.Msg.alert('alert',"setActionBtnAttributes() API called. The 'Save' button is unhidden");                                                             //setActionBtnAttributes() API called
F.setActionBtnAttributes({save:{hidden:false}});
}
if (var1.length==7){
Ext.Msg.alert('alert',"Inline report will be hidden");                                                             //API hides Inline report
F.getObject("INLINEREPORT_24").hide();
}
if (var1.length==8){
Ext.Msg.alert('alert',"Inline report will be shown to user");                                                             //API displays Inline report
F.getObject("INLINEREPORT_24").show();
}
if (var1.length==9){
Ext.Msg.alert('alert',"Link report will be hidden");                                                             //API hides Inline report
F.getObject("REPORT_25").hide();
}
if (var1.length==10){
Ext.Msg.alert('alert',"Link report will be shown to user");                                                             //API displays Inline report
F.getObject("REPORT_25").show();
}
}

