function checkGridAPI_0(){
var var1,row;
var1=F.CID.read();

if (var1.length==1){
Ext.Msg.alert('alert', "Level 1 and level 2 Approver field in Group 1 of Section 10 is hidden");
F.LEVEL_1_APPROVER.hide();
F.LEVEL_2_APPROVER.hide();
}

if (var1.length==2){
Ext.Msg.alert('alert', "Level 1 and level 2 Approver field in Group 1 of Section 10 is shown");
F.LEVEL_1_APPROVER.show();
F.LEVEL_2_APPROVER.show();
}

if (var1.length==3){
F.GR6.addRow(true,true);
}

if (var1.length==4){
var count = F.GR6.getRowCount();
Ext.Msg.alert('alert', "The row count of the grid is : " +count);
}

/*if (var1.length==5){
Ext.Msg.alert('alert', "The dept id field of the grid will be sorted in ascending order");
F.GR6.sort(F.DEPT_ID,'asc');
}*/

if (var1.length==6){
Ext.Msg.alert('alert', "The grid rows will be made empty");
F.GR6.makeEmpty(); 
}
}

