}
if (var2.length==17){
Ext.Msg.alert('alert',"The 'API REPORT' report will be added to Related Reports dropdown");                                                       //showReport() API called
F.addReport("API","API REPORT",{"parameters" :[
{"name":"USERNAME","value":"TEST_USER"}
]});
}
if (var2.length==18){
Ext.Msg.alert('alert',"The report will be hidden");                                                       //hideReport() API called
F.getHeader().hideReport('API REPORT_NO PARAM');
}
if (var2.length==19){
Ext.Msg.alert('alert',"The report will be seen");                                                       //showReport() API called
F.getHeader().showReport('API REPORT_NO PARAM');
}
}

