/******______Part 1________*******/
function checkTreeAPI_1(){
var var1,var2,content;
var max,min,sum,avg;

var1 = F.ORGANIZATION_ID.read();
var row = 1;
/******* Methods ******/
if (var1.length==1){ 
Ext.Msg.alert('alert',"Datatype of the field FEEDBACK: " + F.FEEDBACK.getColumnDataType() + ". Datakind of the field FEEDBACK:" + F.FEEDBACK.getColumnKind());
}

if (var1.length==2){
Ext.Msg.alert('alert',"Name of the field: " + F.FEEDBACK.getName() + ". Label of the field: " + F.FEEDBACK.getLabel());
}

if (var1.length==3){
F.TRANSPORT_NAME.writeAll("Text1","text2");
}

if (var1.length==4){
Ext.Msg.alert('alert',F.TRANSPORT_NAME.readAll());
}

if (var1.length==5){
F.TRANSPORT_NAME.hide(row);
}

if (var1.length==6){
F.TRANSPORT_NAME.show(row);
}

if (var1.length==7){
F.TRANSPORT_NAME.hideAll();
Ext.Msg.alert('alert',"hideAll() API called.");
}

if (var1.length==8){
F.TRANSPORT_NAME.showAll();
Ext.Msg.alert('alert',"showAll() API called.");
}

if (var1.length==9){
F.TRANSPORT_NAME.disable(row);
}

if (var1.length==10){
F.TRANSPORT_NAME.enable(row);
}

if (var1.length==11){
F.TRANSPORT_NAME.disableAll();
Ext.Msg.alert('alert',"disableAll() API called.");
}

if (var1.length==12){
F.TRANSPORT_NAME.enableAll();
Ext.Msg.alert('alert',"enableAll() API called.");
}

if (var1.length==13){
F.TRANSPORT__ADDRESS.hideLabel(1);
Ext.Msg.alert('alert',"hideLabel() API called.");
}

if (var1.length==14){
F.TRANSPORT__ADDRESS.showLabel(1);
Ext.Msg.alert('alert',"showLabel() API called.");
}

if (var1.length==15){
sum = F.TRANSPORT_ID.sum();
avg = F.TRANSPORT_ID.average();
Ext.Msg.alert('alert',"Sum: " + sum + "Average: " + avg );
}

if (var1.length==16){
max = F.TRANSPORT_ID.maximum();
min = F.TRANSPORT_ID.minimum();
Ext.Msg.alert('alert',"Maximum: " + max + "Minimum: " + min );
}

if (var1.length==17){
F.VEICHLE_NAME.makeRequired();
}

if (var1.length==18){
F.VEICHLE_NAME.makeNotRequired();
}

if (var1.length==19){
if(F.FEEDBACK.isEmpty())
   Ext.Msg.alert('alert',"FEEDBACK field is empty");
else
   Ext.Msg.alert('alert',"FEEDBACK field is not empty");
}

if (var1.length==20){
if(F.FEEDBACK.isField())
   Ext.Msg.alert('alert',"FEEDBACK column is a field type");
else
   Ext.Msg.alert('alert',"FEEDBACK column is a not field type");
}


if (var1.length==21){
if(F.VEICHLE_NAME.isMarkedForDeletion(1))
   Ext.Msg.alert('alert',"Row containing VEICHLE_NAME is marked for deletion");
else
   Ext.Msg.alert('alert',"Row containing VEICHLE_NAME is not marked for deletion");
}

if (var1.length==22){
if(F.VEICHLE_NAME.isUnique())
   Ext.Msg.alert('alert',"VEICHLE_NAME column values are unique");
else
   Ext.Msg.alert('alert',"VEICHLE_NAME column values are not unique");
}

if (var1.length==23){
if(F.TRANSPORT_NAME.containsIgnoreCase("bus"))
      Ext.Msg.alert('alert',"Transport name is bus");
}

if (var1.length==24){
content=F.ORGANIZATION_NAME.read();
 Ext.Msg.alert('alert',"The value " + content + " is found at row " + F.DEPARTMENT_NAME.findRow(content));
}

if (var1.length==25){
content=F.ORGANIZATION_NAME.read();
Ext.Msg.alert('alert',"The value " + content + " is found at row " + F.DEPARTMENT_NAME.findRowIgnoreCase(content));
}

if (var1.length==26){
Ext.Msg.alert('alert',"Field "+F.TRANSPORT__ADDRESS.getLabel()+" belongs to a multirow with the id "+F.TRANSPORT__ADDRESS.getMultiRowId());
}

if (var1.length==27){
if(F.DEPARTMENT_NAME.isEmail(1))
    Ext.Msg.alert('alert',"You have entered a valid email id");
else
    Ext.Msg.alert('alert',"Enter a valid email Id");
}

/*
if (var2.length==5){

}
*/
}


/******* Events ******/
F.onLoad(function(){
F.TRANSPORT_ID.write("10");
Ext.Msg.alert('alert',F.TRANSPORT_ID.read());
});

/* onHide */
F.TRANSPORT_NAME.onHide(function(row){
F.VEICHLE_NAME.hide(row);
return true;
});

F.VEICHLE_NAME.onHide(function(row){
F.DEPARTMENT_NAME.hide(row);
return true;
});

F.DEPARTMENT_NAME.onHide(function(row){
Ext.Msg.alert('alert',"hide() and onHide() API called.");
return true;
});

/* onShow */
F.TRANSPORT_NAME.onShow(function(row){
F.VEICHLE_NAME.show(row);
return true;
});

F.VEICHLE_NAME.onShow(function(row){
F.DEPARTMENT_NAME.show(row);
return true;
});

F.DEPARTMENT_NAME.onShow(function(row){
Ext.Msg.alert('alert',"show() and onShow() API called.");
return true;
});

/* onEnable */
F.TRANSPORT_NAME.onEnabled(function(row){
F.VEICHLE_NAME.enable(row);
return true;
});

F.VEICHLE_NAME.onEnabled(function(row){
F.DEPARTMENT_NAME.enable(row);
return true;
});

F.DEPARTMENT_NAME.onEnabled(function(row){
Ext.Msg.alert('alert',"enable() and onEnabled() API called.");
return true;
});

/* onDisable */
F.TRANSPORT_NAME.onDisabled(function(row){
F.VEICHLE_NAME.disable(row);
return true;
});

F.VEICHLE_NAME.onDisabled(function(row){
F.DEPARTMENT_NAME.disable(row);
return true;
});

F.DEPARTMENT_NAME.onDisabled(function(row){
Ext.Msg.alert('alert',"disable() and onDisabled () API called.");
return true;
});

F.TRANSPORT_NAME.onChange(function(row) 
{ 
if(F.TRANSPORT_NAME.read().length==1) {
   if(F.TRANSPORT_NAME.isRequired())
       Ext.Msg.alert('alert',"This is a mandatory field!");
   else
       Ext.Msg.alert('alert',"This is not a mandatory field!");
}
return true;
//Ext.Msg.alert('alert',"You are about to change the value of the field!");
});

F.VEICHLE_NAME.onRequired(function(row){
Ext.Msg.alert('alert',"makeRequired() and onRequired() API called.");
return true;
});

F.VEICHLE_NAME.onNotRequired(function(row){
Ext.Msg.alert('alert',"makeNotRequired() and onNotRequired() API called.");
return true;
});

F.onSave(function(){
if(F.TRANSPORT_NAME.contains("car"))
      Ext.Msg.alert('alert',"Transport name is car");
if(F.VEICHLE_NAME.contains("alto"))
      Ext.Msg.alert('alert',"Vehicle name is Alto type");
});
